const config = require('$custom/config');
const client = config.arg == 'default' ? 'http://localhost:811' : 'http://localhost:8200';
const socket = require('socket.io-client')(client);

module.exports = {
	client: function(){
		return socket;
	},
	api: function(){
		socket.emit('no-client', {});
		return socket;
	},
	events: {
		
	}
}